import "./App.css";
import React from "react";
import Nav from "./components/Nav";
import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";
import logo from "./img/myLogoTransSmall.png";

class App extends React.Component {
  constructor() {
    super();
    this.headers = [
      {
        id: "weeks",
        name: "Week",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-weeks",
      },
      {
        id: "photos",
        name: "Photos",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-photos",
      },
      {
        id: "articles",
        name: "Articles",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-articles",
      },
      {
        id: "projects",
        name: "Projects",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-projects",
      },
      {
        id: "aboutMe",
        name: "About Me",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-aboutMe",
      },
    ];
    this.state = {
      currentID: null,
      currentTab: null,
    };
  }

  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }
  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    if (window.scrollY > 0) {
      document.querySelector(".et-hero-tabs-container").className =
        "et-hero-tabs-container scroll";
    } else {
      document.querySelector(".et-hero-tabs-container").className =
        "et-hero-tabs-container";
    }
  };

  render() {
    /*
    ===========================================================
                    Helper Functions
    ===========================================================
    */
    const mapHelperNav = (nav, index) => {
      return <Nav href={nav.href} name={nav.name} key={index} />;
    };
    const mapHelperHeader = (header, index) => {
      return (
        <Header
          id={header.id}
          name={header.name}
          slogan={header.slogan}
          key={index}
        />
      );
    };
    /*
    ===========================================================
                             Display
    ===========================================================
    */
    return (
      <div className="App container">
        {/**************** Nav Bar **************************/}
        <section className="et-hero-tabs">
          <div class="sticky-top">
          <h1>Marquise Piton</h1>
          <h3>I build websites as If I am telling a story</h3>
            <div className="et-hero-tabs-container" sticky="top">
              <a className="et-hero-tab" alt="Logo" href="home" key="">
                <img src={logo} alt="Logo" width="65" height="65" />
              </a>
            </div>

            {this.headers.map(mapHelperNav)}
            <span className="et-hero-tab-slider"></span>
          </div>
        </section>
        {/****************************************************/}
        {/**************** Heroes ****************************/}
        <main className="et-main">{this.headers.map(mapHelperHeader)}</main>
        {/****************************************************/}
      </div>
    );
  }
}
export default App;
