import "./App.css";
import React from "react";
import Nav from "./components/Nav";
import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";

class App extends React.Component {
  constructor() {
    super();
    this.headers = [
      {
        id: "tab-home",
        name: "Home",
        slogan: "I build websites as I am telling a story",
      },
      {
        id: "tab-weeks",
        name: "Week",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
      },
      {
        id: "tab-photos",
        name: "Photos",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
      },
      {
        id: "tab-articles",
        name: "Articles",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
      },
      {
        id: "tab-projects",
        name: "Projects",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
      },
      {
        id: "tab-aboutMe",
        name: "About Me",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
      },
    ];
  }

  render() {
    /*
    ===========================================================
                    Helper Functions
    ===========================================================
    */
    const mapHelper = (header, index) => {
      return (
        <Nav
          id={this.headers.id}
          name={this.headers.name}
          slogan={this.headers.slogan}
        />
      );
    };
    return (
      <div>
        <div className="App container">
         {this.header.map(mapHelper)}  
          <Header />
        </div>
      </div>
    );
  }
}
export default App;
