import "./App.css";
import React from "react";
import Nav from "./components/Nav";
import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";

class App extends React.Component {
  constructor() {
    super();
    this.headers = [
      // {
      //   id: "tab-home",
      //   name: "Home",
      //   slogan: "I build websites as I am telling a story",
      //   href: "#tab-home",
      // },
      {
        id: "weeks",
        name: "Week",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-weeks",
      },
      {
        id: "photos",
        name: "Photos",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-photos",
      },
      {
        id: "articles",
        name: "Articles",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-articles",
      },
      {
        id: "projects",
        name: "Projects",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-projects",
      },
      {
        id: "aboutMe",
        name: "About Me",
        slogan: "I do into detail each with my time the Awesome Inc Bootcamp",
        href: "#tab-aboutMe",
      },
    ];
  }

  render() {
    /*
    ===========================================================
                    Helper Functions
    ===========================================================
    */
    const mapHelperNav = (nav, index) => {
      return <Nav href={nav.href} name={nav.name} key={index} />;
    };
    const mapHelperHeader = (header, index) => {
      return (
        <Header
          id={header.id}
          name={header.name}
          slogan={header.slogan}
          key={index}
        />
      );
    };
    /*
    ===========================================================
                    Display
    ===========================================================
    */
    return (
      <div className="App container">
        {/**************** Nav Bar **************************/}
        <section class="et-hero-tabs">
          <h1>Marquise Piton</h1>
          <h3>I build websites as If I am telling a story</h3>
          <div className="et-hero-tabs-container">
            {this.headers.map(mapHelperNav)}
            <span class="et-hero-tab-slider"></span>
          </div>
        </section>
        {/****************************************************/}
        {/**************** Heroes ****************************/}
        <main class="et-main">{this.headers.map(mapHelperHeader)}</main>
        {/****************************************************/}
      </div>
    );
  }
}
export default App;
